import React, { useState, useEffect } from 'react';
import LawSearchResults from './LawSearchResults';

const SearchPage = () => {
  const [query, setQuery] = useState('');
  const [filter, setFilter] = useState('');
  const [submittedQuery, setSubmittedQuery] = useState('');
  const [submittedFilter, setSubmittedFilter] = useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    setSubmittedQuery(query);
    setSubmittedFilter(filter);
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Search US Cybersecurity Laws</h1>

      {/* Search Bar + Filter */}
      <form onSubmit={handleSearch} className="flex flex-col sm:flex-row gap-2 mb-4">
        <input
          type="text"
          placeholder="Search by keyword..."
          className="flex-1 border p-2 rounded"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />

        <select
          className="border p-2 rounded"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="">All Categories</option>
          <option value="Compliance">Compliance</option>
          <option value="Data Privacy">Data Privacy</option>
          <option value="Cybercrime Consequences">Cybercrime Consequences</option>
        </select>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Search
        </button>
      </form>

      {/* Results */}
      <LawSearchResults query={submittedQuery} filter={submittedFilter} />
    </div>
  );
};

export default SearchPage;
