import React, { useState } from 'react';
import axios from 'axios';

const LawSearch = ({ user }) => {
  const [query, setQuery] = useState('');
  const [filter, setFilter] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    const res = await axios.get(`/api/laws?query=${query}&filter=${filter}`);
    setResults(res.data);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Search US Cybersecurity Laws</h1>
      <p className="mb-4 text-sm text-gray-600">Logged in as: <strong>{user}</strong></p>

      <div className="flex flex-col md:flex-row gap-4 mb-6">
        <input
          type="text"
          placeholder="Enter keyword..."
          className="flex-1 p-2 border rounded"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <select
          className="p-2 border rounded"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="">All Categories</option>
          <option value="Compliance">Compliance Laws</option>
          <option value="Privacy">Data Privacy Laws</option>
          <option value="Hacking">Hacking Consequences</option>
        </select>
        <button
          onClick={handleSearch}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Search
        </button>
      </div>

      <div>
        {results.length > 0 ? (
          results.map((law) => (
            <div key={law.id} className="border-b py-2">
              <a href={law.link} className="text-blue-500 font-semibold" target="_blank" rel="noreferrer">
                {law.title}
              </a>
              <p className="text-sm">{law.description}</p>
              <button
                onClick={async () => {
                  const res = await axios.get(`/api/laws/${law.id}/citation`);
                  navigator.clipboard.writeText(res.data.citation);
                  alert("Citation copied to clipboard!");
                }}
                className="text-sm text-gray-600 underline mt-1"
              >
                Copy MLA Citation
              </button>
            </div>
          ))
        ) : (
          <p>No results found.</p>
        )}
      </div>
    </div>
  );
};

export default LawSearch;
