import React, { useState, useEffect } from 'react';

const LawSearch = ({ query, filter }) => {
  const [laws, setLaws] = useState([]);
  const [citations, setCitations] = useState({});

  useEffect(() => {
    fetch(`/api/laws?query=${query}&filter=${filter}`)
      .then(res => res.json())
      .then(data => setLaws(data))
      .catch(err => console.error('Error fetching laws:', err));
  }, [query, filter]);

  const fetchCitation = async (lawId) => {
    try {
      const res = await fetch(`/api/laws/${lawId}/citation`);
      const data = await res.json();
      setCitations(prev => ({ ...prev, [lawId]: data.citation }));
    } catch (err) {
      console.error('Error fetching citation:', err);
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text).then(() => {
      alert('Citation copied to clipboard!');
    });
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Search Results</h2>
      {laws.length === 0 ? (
        <p>No laws found.</p>
      ) : (
        <ul>
          {laws.map((law) => (
            <li key={law.id} className="mb-6 border-b pb-4">
              <h3 className="text-lg font-bold">{law.title}</h3>
              <p>{law.description}</p>
              <p><strong>Category:</strong> {law.category}</p>
              <a
                href={law.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 underline"
              >
                View Full Law
              </a>

              <div className="mt-2">
                {!citations[law.id] ? (
                  <button
                    onClick={() => fetchCitation(law.id)}
                    className="bg-blue-500 text-white px-3 py-1 rounded"
                  >
                    Show Citation
                  </button>
                ) : (
                  <>
                    <p className="italic mt-2">{citations[law.id]}</p>
                    <button
                      onClick={() => copyToClipboard(citations[law.id])}
                      className="bg-green-500 text-white px-3 py-1 mt-1 rounded"
                    >
                      Copy Citation
                    </button>
                  </>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default LawSearch;
